// Nodo Formattazione modificato - Template email professionale CORRETTO

// Leggi il messaggio dei vincoli e i dati catastali
const messaggioVincoli = items[0]?.json?.messaggio_vincoli;
const foglio = $('Information Extractor').item.json.output.foglio;
const mappale = $('Information Extractor').item.json.output.mappale;

// Controllo di sicurezza
if (!messaggioVincoli || !foglio || !mappale) {
  return [{
    json: {
      error: 'Dati mancanti per la formattazione email.'
    }
  }];
}

// Mappatura dei vincoli ai riferimenti legali completi
const vincoliMapping = {
    'costa': 'DLgs 42/2004, articolo 142, comma1, lettera a) - Territori costieri.',
    'laghi': 'DLgs 42/2004, articolo 142, comma1, lettera b) - Territori contermini ai laghi.',
    'fiumi': 'DLgs 42/2004, articolo 142, comma1, lettera c) - Fiumi, torrenti, corsi d\'acqua.',
    'contigua': 'DLgs 42/2004, articolo 142, comma1, lettera f) - Parco Regionale e relativa area contigua.',
    'boschi': 'DLgs 42/2004, articolo 142, comma1, lettera g) - Area e territori coperti da foreste e boschi.',
    'interesse_archeologico': 'DLgs 42/2004, articolo 142, comma1, lettera m) - Zone di interesse archeologico.',
    'capitana': 'DLgs 42/2004, articolo 136, lettera d) - DM 13/2019 - Zona a nord dell\'abitato di Magliano in Toscana - Colline della Capitana.',
    'parco': 'DLgs 42/2004, articolo 136 - DM 26/09/1962 - Zona dei Monti dell\'Uccellina, compresa parte della limitrofa pianura.',
    'pereta': 'DLgs 42/2004, articolo 136 - DM 16/05/1974 - Zona di Pereta sita nel territorio del comune di Magliano in Toscana.',
    'magliano': 'DLgs 42/2004, articolo 136 - DM 07/12/1973 - Centro abitato e zone circostanti del comune di Magliano in Toscana.',
    'idrogeologico': 'Vincolo idrogeologico'
};

// Funzione che formatta il messaggio in HTML professionale
function formatEmailHtml(messaggio, foglio, mappale) {
  // Crea la struttura base dell'email
  const baseHtml = `
    <!DOCTYPE html>
    <html>
    <head>
      <meta charset="UTF-8">
      <title>Vincoli Paesaggistici - Comune di Magliano in Toscana</title>
    </head>
    <body style="font-family: Arial, sans-serif; line-height: 1.6; margin: 0; padding: 20px; background-color: #f9f9f9;">
      <div style="max-width: 700px; margin: 0 auto; background: white; border: 1px solid #ddd; border-radius: 5px; padding: 30px;">

        <div style="text-align: center; border-bottom: 2px solid #333; padding-bottom: 15px; margin-bottom: 25px;">
          <h1 style="margin: 0; font-size: 20px; color: #333;">
            Comune di Magliano in Toscana
          </h1>
          <p style="margin: 5px 0 0 0; font-size: 14px; color: #666;">
            Settore Tecnico
          </p>
        </div>

        <div>
          <p><strong>Oggetto:</strong> Rilascio informazioni vincoli paesaggistici e idrogeologico - Foglio ${foglio} Mappale ${mappale}</p>

          <p>Gentile richiedente,</p>

          <p>con riferimento alla sua richiesta di informazioni sui vincoli paesaggistici e idrogeologici, si comunica quanto segue:</p>

          <p><strong>ESITO DELLA RICERCA:</strong><br>
          Il nostro sistema ha elaborato la sua domanda relativa al foglio catastale ${foglio} e mappale ${mappale} e dalle informazioni che ci ha fornito risulta quanto segue:</p>

          <!-- SPAZIO PER IL CONTENUTO VARIABILE -->

          <p><strong>VALIDITÀ DELLE INFORMAZIONI:</strong><br>
          I dati forniti sono estratti dal database ufficiale del Comune e hanno valore informativo alla data odierna.</p>

          <p>Si resta a disposizione per ogni chiarimento.</p>

          <div style="margin-top: 30px; text-align: right;">
            <p style="margin: 0;"><strong>Arch. Bartoli Leonardo</strong></p>
            <p style="margin: 0; font-style: italic; font-size: 14px;">Responsabile Settore Tecnico</p>
            <p style="margin: 0; font-size: 14px;">Comune di Magliano in Toscana</p>
          </div>
        </div>

      </div>
    </body>
    </html>
  `;

  let variabileContenuto = '';

  // Caso 1: Foglio/mappale non trovati nel database
  if (messaggio.includes('non risultano dati')) {
    variabileContenuto = `
      <p>I dati forniti non producono risultati (dati inesatti o particelle che nel frattempo sono state frazionate, eliminate, sostituite con nuove, ecc.)</p>
    `;
  }
  // Caso 2: Nessun vincolo trovato per il foglio/mappale esistente - PATTERN MULTIPLI
  else if (messaggio.includes('Nessun vincolo paesaggistico o idrogeologico presente') || 
           messaggio.includes('non sono stati trovati vincoli paesaggistici né idrogeologici')) {
    variabileContenuto = `
      <p><strong>Nessun vincolo paesaggistico o idrogeologico presente.</strong></p>
    `;
  }
  // Caso 3: Vincoli presenti
  else if (messaggio.includes('sono presenti i seguenti vincoli paesaggistici:')) {
    const vincoliPaesPart = messaggio.split('Vincoli Paesaggistici:')[1].split('Vincolo Idrogeologico:')[0].trim();
    const vincoloIdroPart = messaggio.split('Vincolo Idrogeologico:')[1].trim();

    let vincoliPaesList = '';
    if (vincoliPaesPart.length > 0) {
        // Converti i nomi semplici in riferimenti legali completi
        const vincoliPaesArray = vincoliPaesPart.split(',').map(v => v.trim().toLowerCase());
        const mappedVincoli = vincoliPaesArray.map(v => `<li>${vincoliMapping[v] || v}</li>`).join('');
        vincoliPaesList = `
            <ul>
                ${mappedVincoli}
            </ul>
        `;
    } else {
        vincoliPaesList = '<p>Nessun vincolo paesaggistico presente.</p>';
    }

    let vincoloIdroList = '';
    if (vincoloIdroPart.length > 0) {
        // Mappa il nome semplice a un riferimento legale, se presente
        const mappedIdro = vincoloIdroPart.toLowerCase();
        vincoloIdroList = `
            <ul>
                <li>${vincoliMapping[mappedIdro] || mappedIdro}</li>
            </ul>
        `;
    } else {
        vincoloIdroList = '<p>Nessun vincolo idrogeologico presente.</p>';
    }

    if (vincoliPaesPart.length > 0 || vincoloIdroPart.length > 0) {
      variabileContenuto = `
        <p><strong>L'area è soggetta ai seguenti vincoli:</strong></p>
        <p><strong>Vincoli Paesaggistici:</strong></p>${vincoliPaesList}
        <p><strong>Vincolo Idrogeologico:</strong></p>${vincoloIdroList}
      `;
    }
  }

  // Inserisci il contenuto variabile nella struttura base
  return baseHtml.replace('<!-- SPAZIO PER IL CONTENUTO VARIABILE -->', variabileContenuto);
}

// Genera l'HTML formattato
const emailHtml = formatEmailHtml(messaggioVincoli, foglio, mappale);

// Restituisci l'output
return [{
  json: {
    email_html: emailHtml,
    messaggio_vincoli: messaggioVincoli
  }
}];

// Verifica se ci sono risultati dal database
const inputData = $input.all();

// Recupera foglio e mappale dal nodo Information Extractor
const foglio = $('Information Extractor').item.json.output.foglio;
const mappale = $('Information Extractor').item.json.output.mappale;

let messaggioVincoli = '';

// Controlla se sono stati trovati record nel database
if (!inputData || inputData.length === 0) {
    // Nessun record trovato - foglio/mappale non esistente
    messaggioVincoli = `non risultano dati, il foglio catastale ${foglio} e il mappale ${mappale} non producono risultati (dati inesatti o particelle che nel frattempo sono state frazionate, eliminate, sostituite con nuove, ecc.)`;
} else {
    // Record trovato, verifica se ha un ID valido
    const firstItem = inputData[0]?.json;

    if (!firstItem || !firstItem.id) {
        // Record vuoto o senza ID - foglio/mappale non esistente
        messaggioVincoli = `non risultano dati, il foglio catastale ${foglio} e il mappale ${mappale} non producono risultati (dati inesatti o particelle che nel frattempo sono state frazionate, eliminate, sostituite con nuove, ecc.)`;
    } else {
        // Record valido trovato, verifica i vincoli
        const vincoliPaesaggistici = [
            firstItem.paes_interesse_archeologico,
            firstItem.paes_boschi,
            firstItem.paes_costa,
            firstItem.paes_fiumi,
            firstItem.paes_laghi,
            firstItem.paes_area_contigua_parco,
            firstItem.paes_dm_capitana,
            firstItem['paes__dm_parco'],
            firstItem.paes_dm_pereta,
            firstItem.paes_dm_magliano
        ]
        .filter(item => item && item.trim() !== '' && item !== null && item !== undefined)
        .join(', ');

        const vincoloIdrogeologico = firstItem.vincolo_idrogeologico && firstItem.vincolo_idrogeologico.trim() !== '' ? firstItem.vincolo_idrogeologico : '';

        // Controlla se ci sono vincoli presenti
        if (vincoliPaesaggistici.length > 0 || vincoloIdrogeologico.length > 0) {
            messaggioVincoli = `sono presenti i seguenti vincoli paesaggistici: Vincoli Paesaggistici: ${vincoliPaesaggistici} Vincolo Idrogeologico: ${vincoloIdrogeologico}`; 
        } else {
            // Record esistente ma senza vincoli
            messaggioVincoli = `per il foglio catastale ${foglio} e il mappale ${mappale} non sono stati trovati vincoli paesaggistici né idrogeologici.`;
        }
    }
}

return [{
  json: {
    messaggio_vincoli: messaggioVincoli,
    foglio: foglio,
    mappale: mappale,
    records_found: inputData.length
  }
}];
