  const id = json.id_segnalazione ?? "—";
  const oggetto = json.oggetto ?? "";
  const descrizione = json.descrizione ?? "";
  const stato = json.stato ?? "";

  html += `
    <li>
      <b>Segnalazione #${id}</b><br>
      <b>Oggetto:</b> ${oggetto}<br>
      <b>Descrizione:</b> ${descrizione}<br>
      <b>Stato:</b> ${stato}
    </li>
  `;
}
html += "</ul>";

return [{
  json: {
    resoconto_mail: html,
    count: all.length
  }
}];

