// Code node to merge protocol number with AI output for Qdrant
const protocolData = $input.item.json; // From "Generazione protocollo"
const aiOutput = $('Estrattore e Classificatore Segnalazione1').item.json.output;

// Parse the AI output if it's JSON
let parsedOutput;
try {
  const cleanOutput = aiOutput.replace(/```json\s*/, '').replace(/```\s*$/, '').trim();
  parsedOutput = JSON.parse(cleanOutput);
} catch (error) {
  // If parsing fails, create a basic structure
  parsedOutput = {
    output: aiOutput,
    oggetto: 'Segnalazione generica',
    descrizione: aiOutput,
    risposta_cittadino: 'Risposta standard',
    nome: 'Non specificato',
    cognome: 'Non specificato',
    telefono: 'Non fornito',
    email: 'non.fornita@comune.maglianointoscana.gr.it',
    indirizzo: 'Da definire',
    canale: 'Telefono',
    data: new Date().toISOString().split('T')[0],
    tipologia: 'Lavori Pubblici',
    urgenza: 'Media',
    dati_catastali: null,
    ufficio_competente: 'Lavori Pubblici',
    priorita: 'Media',
    telefono_ufficio: '0564 593439',
    email_ufficio: 'tecnico@comune.maglianointoscana.gr.it',
    tempi_gestione: '5-7 giorni lavorativi',
    documenti_richiesti: 'Da verificare',
    note_interne: 'Segnalazione creata automaticamente, dati da verificare.'
  };
}

// Add the protocol number to the parsed output
parsedOutput.numero_protocollo = protocolData.numero_protocollo;

// Update risposta_cittadino with correct protocol number if it exists
if (parsedOutput.risposta_cittadino && parsedOutput.risposta_cittadino.includes('MAGL-')) {
  const today = new Date().toISOString().split('T')[0];
  parsedOutput.risposta_cittadino = `Gentile Cittadino/a, confermiamo la ricezione della segnalazione del ${today}. La pratica è stata assegnata all'ufficio ${parsedOutput.ufficio_competente || 'Lavori Pubblici'} con numero di protocollo ${protocolData.numero_protocollo}. Contatteremo per completare i dati mancanti. Per informazioni: 0564 593439 negli orari Lunedì-Mercoledì 9:00-12:00, Martedì-Giovedì 16:00-17:00. Cordiali saluti, Comune di Magliano in Toscana`;
}

// Return the merged data for Qdrant processing
return {
  json: {
    output: parsedOutput,
    numero_protocollo: protocolData.numero_protocollo,
    ...parsedOutput
  }
};

// Funzione per generare valori di fallback
function generateFallbacks() {
  const today = new Date().toISOString().split('T')[0];
  const randomProtocol = Math.floor(1000 + Math.random() * 9000);

  return {
    nome: 'Non specificato',
    cognome: 'Non specificato', 
    telefono: 'Non fornito',
    email: 'non.fornita@comune.maglianointoscana.gr.it',
    indirizzo: 'Da definire',
    canale: 'Telefono',
    data: today,
    tipologia: 'Lavori Pubblici',
    oggetto: 'Segnalazione generica',
    descrizione: 'Dati incompleti - richiedere integrazione',
    urgenza: 'Media',
    dati_catastali: null,
    ufficio_competente: 'Lavori Pubblici',
    priorita: 'Media',
    telefono_ufficio: '0564 593439',
    email_ufficio: 'tecnico@comune.maglianointoscana.gr.it',
    tempi_gestione: '5-7 giorni lavorativi',
    numero_protocollo: 'MAGL-2024-' + randomProtocol,
    documenti_richiesti: 'Da verificare',
    risposta_cittadino: `Gentile Cittadino/a, confermiamo la ricezione della segnalazione del ${today}. La pratica è stata assegnata all'ufficio Lavori Pubblici con numero di protocollo MAGL-2024-${randomProtocol}. Contatteremo per completare i dati mancanti. Per informazioni: 0564 593439 negli orari Lunedì-Mercoledì 9:00-12:00, Martedì-Giovedì 16:00-17:00. Cordiali saluti, Comune di Magliano in Toscana`,
    note_interne: 'Segnalazione creata automaticamente, dati da verificare.'
  };
}

// Funzione per pulire e validare i dati
function validateAndClean(data, fallbacks) {
  const result = { ...fallbacks };

  for (const [key, value] of Object.entries(data)) {
    const trimmedValue = (typeof value === 'string') ? value.trim() : value;
    if (trimmedValue !== null && trimmedValue !== undefined && trimmedValue !== '' && trimmedValue !== 'null' && !['Non specificato', 'Non fornito'].includes(trimmedValue)) {
      if (key === 'tipologia') {
        const allowedTypes = ['Lavori Pubblici', 'Patrimonio', 'Edilizia Privata', 'Paesaggistico', 'Ambiente', 'Urbanistica', 'Protezione Civile'];
        result[key] = allowedTypes.includes(trimmedValue) ? trimmedValue : 'Lavori Pubblici';
      } else if (key === 'priorita' || key === 'urgenza') {
        const allowedPriorities = ['Alta', 'Media', 'Bassa'];
        result[key] = allowedPriorities.includes(trimmedValue) ? trimmedValue : 'Media';
      } else if (key === 'canale') {
        const allowedChannels = ['Telefono', 'Email', 'WhatsApp'];
        result[key] = allowedChannels.includes(trimmedValue) ? trimmedValue : fallbacks.canale;
      } else if (key === 'data') {
        const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
        result[key] = dateRegex.test(trimmedValue) ? trimmedValue : fallbacks.data;
      } else if (key === 'email' && trimmedValue.includes('@')) {
        result[key] = trimmedValue;
      } else if (key === 'telefono' && (trimmedValue.includes('-') || trimmedValue.length >= 7)) {
        result[key] = trimmedValue;
      } else if (typeof trimmedValue === 'string') {
        result[key] = trimmedValue;
      }
    }
  }

  return result;
}

// MODIFICA PRINCIPALE: Ottenere il numero protocollo dal nodo "Merge Protocol Data"
const protocolloData = $input.item.json;
const numeroProtocollo = protocolloData.numero_protocollo;

let rawOutput = protocolloData.ai_output || '';
rawOutput = rawOutput.replace(/```json\s*/, '').replace(/```\s*$/, '').trim();

const fallbacks = generateFallbacks();
// Sovrascrivi con il numero protocollo generato
if (numeroProtocollo) {
  fallbacks.numero_protocollo = numeroProtocollo;
  // Aggiorna anche la risposta cittadino con il numero corretto
  const today = new Date().toISOString().split('T')[0];
  fallbacks.risposta_cittadino = `Gentile Cittadino/a, confermiamo la ricezione della segnalazione del ${today}. La pratica è stata assegnata all'ufficio Lavori Pubblici con numero di protocollo ${numeroProtocollo}. Contatteremo per completare i dati mancanti. Per informazioni: 0564 593439 negli orari Lunedì-Mercoledì 9:00-12:00, Martedì-Giovedì 16:00-17:00. Cordiali saluti, Comune di Magliano in Toscana`;
}

try {
  const parsed = JSON.parse(rawOutput);
  const validated = validateAndClean(parsed, fallbacks);
  // Assicura che il numero protocollo sia sempre quello generato
  validated.numero_protocollo = numeroProtocollo || validated.numero_protocollo;
  return { json: validated };
} catch (error) {
  const jsonMatch = rawOutput.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    try {
      const parsed = JSON.parse(jsonMatch[0]);
      const validated = validateAndClean(parsed, fallbacks);
      // Assicura che il numero protocollo sia sempre quello generato
      validated.numero_protocollo = numeroProtocollo || validated.numero_protocollo;
      return { json: validated };
    } catch (e) {
      return { json: { 
        ...fallbacks,
        descrizione: `Errore parsing: ${rawOutput.substring(0, 200)}...`,
        oggetto: 'Errore elaborazione dati',
        numero_protocollo: numeroProtocollo || fallbacks.numero_protocollo
      }};
    }
  }

  // Assicura che anche nei fallback il numero protocollo sia corretto
  fallbacks.numero_protocollo = numeroProtocollo || fallbacks.numero_protocollo;
  return { json: fallbacks };
}

// Recupera l'ultimo numero dal nodo precedente (Postgres "Ottieni Protocollo")
const numero = $json.ultimo_numero;
const anno = new Date().getFullYear();

// Costruisci il protocollo con zeri iniziali (es: MAGL-2025-0001)
const protocollo = `MAGL-${anno}-${String(numero).padStart(4, '0')}`;

// Ritorna i dati originali + nuovo campo numero_protocollo
return [{
  json: {
    ...$json,
    numero_protocollo: protocollo
  }
}];

// La funzione accetta un array di elementi, in questo caso uno solo
for (const item of items) {

  // Accedi ai campi specifici dall'oggetto di input
  const oggetto = item.json.oggetto || 'Segnalazione generica';
  const descrizione = item.json.descrizione || 'Descrizione non disponibile';
  const rispostaCittadino = item.json.risposta_cittadino || 'Risposta standard';
  const numeroProtocollo = item.json.numero_protocollo || 'N/A';
  const tipologia = item.json.tipologia || 'Lavori Pubblici';
  const urgenza = item.json.urgenza || 'Media';
  const indirizzo = item.json.indirizzo || 'Da definire';

  // Concatena i campi in un'unica stringa leggibile
  const testoCompleto = `Protocollo: ${numeroProtocollo}. Tipologia: ${tipologia}. Oggetto: ${oggetto}. Descrizione: ${descrizione}. Luogo: ${indirizzo}. Urgenza: ${urgenza}. Risposta al cittadino: ${rispostaCittadino}.`;

  // Crea un nuovo oggetto con la chiave 'text' e la stringa combinata
  const jsonlOutput = {
    text: testoCompleto
  };

  // Sostituisci il vecchio item con il nuovo oggetto JSONL
  item.json = jsonlOutput;
}

// Restituisci l'array di elementi modificato, pronto per l'output
return items;

// Formattazione email con gestione problemi già noti
const all = $input.all();
console.log('Input ricevuto dall\'AI Agent:', JSON.stringify(all, null, 2));

if (all.length === 0) {
  return [{
    json: {
      resoconto_mail: "<p><i>❌ Nessuna segnalazione trovata per i criteri specificati.</i></p>",
      count: 0,
      hasResults: false
    }
  }];
}

const aiOutput = all[0].json.output || all[0].json.text || '';
console.log('Testo dall\'AI da formattare:', aiOutput);

if (aiOutput.toLowerCase().includes('nessuna segnalazione')) {
  return [{
    json: {
      resoconto_mail: "<p><i>❌ Nessuna segnalazione trovata per i criteri specificati.</i></p>",
      count: 0,
      hasResults: false
    }
  }];
}

// Rileva se il problema è già noto
const problemaGiaSegnalato = aiOutput.toLowerCase().includes('problema_gia_segnalato: si') ||
                            aiOutput.toLowerCase().includes('numero_segnalazioni_precedenti');

let htmlFormatted = '';
let segnalazioniCount = 0;
let problemaNottoInfo = {};

// Estrai informazioni sul problema già noto
if (problemaGiaSegnalato) {
  const numeroMatch = aiOutput.match(/NUMERO_SEGNALAZIONI_PRECEDENTI:\s*([^\n]+)/i);
  const primoMatch = aiOutput.match(/PRIMO_RILEVAMENTO:\s*([^\n]+)/i);
  const ufficioMatch = aiOutput.match(/UFFICIO_COMPETENTE:\s*([^\n]+)/i);
  const statoMatch = aiOutput.match(/STATO_INTERVENTO:\s*([^\n]+)/i);
  const soluzioniMatch = aiOutput.match(/SOLUZIONI_ADOTTATE:\s*([^\n]+)/i);
  const prossimiMatch = aiOutput.match(/PROSSIMI_PASSI:\s*([^\n]+)/i);
  const comunicazioneMatch = aiOutput.match(/COMUNICAZIONE_CITTADINO:\s*([^\n]+)/i);

  problemaNottoInfo = {
    numeroSegnalazioni: numeroMatch ? numeroMatch[1].trim() : '',
    primoRilevamento: primoMatch ? primoMatch[1].trim() : '',
    ufficioCompetente: ufficioMatch ? ufficioMatch[1].trim() : 'Settore Tecnico',
    statoIntervento: statoMatch ? statoMatch[1].trim() : '',
    soluzioniAdottate: soluzioniMatch ? soluzioniMatch[1].trim() : '',
    prossimiPassi: prossimiMatch ? prossimiMatch[1].trim() : '',
    comunicazione: comunicazioneMatch ? comunicazioneMatch[1].trim() : ''
  };
}

// Splitta il testo in paragrafi
const paragraphs = aiOutput.split('\n\n').filter(p => p.trim());

for (const paragraph of paragraphs) {
  const cleanParagraph = paragraph.trim();

  // Se contiene segnalazione
  if (cleanParagraph.toLowerCase().includes('segnalazione') && 
      !cleanParagraph.toLowerCase().includes('problema_gia_segnalato')) {
    segnalazioniCount++;

    const lines = cleanParagraph.split('\n').map(line => line.trim()).filter(line => line);

    htmlFormatted += `
    <div style="margin-bottom: 20px; padding: 15px; border-left: 4px solid #007bff; background: #f8f9fa; border-radius: 5px;">
      <h4 style="color: #007bff; margin-top: 0;">🎯 ${lines[0]}</h4>
    `;

    // Formatta le righe successive con nuovi campi
    for (let i = 1; i < lines.length; i++) {
      const line = lines[i];

      if (line.toLowerCase().startsWith('oggetto:')) {
        htmlFormatted += `<p><strong>📋 ${line}</strong></p>`;
      } else if (line.toLowerCase().startsWith('descrizione:')) {
        htmlFormatted += `<p><strong>📝 ${line}</strong></p>`;
      } else if (line.toLowerCase().startsWith('stato:')) {
        const stato = line.substring(6).trim();
        const color = stato.toLowerCase().includes('completat') ? '#28a745' : 
                     stato.toLowerCase().includes('lavorazione') || stato.toLowerCase().includes('corso') ? '#ffc107' : '#dc3545';
        htmlFormatted += `<p><strong>📊 Stato:</strong> <span style="padding: 3px 8px; border-radius: 3px; color: white; background: ${color}">${stato}</span></p>`;
      } else if (line.toLowerCase().startsWith('data:')) {
        htmlFormatted += `<p><strong>📅 ${line}</strong></p>`;
      } else if (line.toLowerCase().startsWith('luogo:') || line.toLowerCase().startsWith('indirizzo:')) {
        htmlFormatted += `<p><strong>📍 ${line}</strong></p>`;
      } else if (line.toLowerCase().startsWith('segnalatore:')) {
        htmlFormatted += `<p><strong>👤 ${line}</strong></p>`;
      } else if (line.toLowerCase().startsWith('ufficio_competente:')) {
        const ufficio = line.substring('ufficio_competente:'.length).trim();
        htmlFormatted += `<p><strong>🏢 Ufficio competente:</strong> <span style="background: #e3f2fd; padding: 2px 6px; border-radius: 3px;">${ufficio}</span></p>`;
      } else if (line.toLowerCase().startsWith('azioni_intraprese:')) {
        const azioni = line.substring('azioni_intraprese:'.length).trim();
        htmlFormatted += `<p><strong>⚡ Azioni intraprese:</strong> ${azioni}</p>`;
      } else if (line.length > 5) {
        htmlFormatted += `<p>${line}</p>`;
      }
    }

    htmlFormatted += `</div>`;
  }
  // Altri paragrafi importanti (escludi i metadati)
  else if (cleanParagraph.length > 20 && 
           !cleanParagraph.toLowerCase().includes('problema_gia_segnalato') &&
           !cleanParagraph.toLowerCase().includes('numero_segnalazioni') &&
           !cleanParagraph.toLowerCase().includes('ufficio_competente') &&
           !cleanParagraph.toLowerCase().includes('comunicazione_cittadino')) {
    htmlFormatted += `<p style="margin-bottom: 10px;">${cleanParagraph.replace(/\n/g, '<br>')}</p>`;
  }
}

// Se non abbiamo trovato segnalazioni strutturate
if (segnalazioniCount === 0 && aiOutput.length > 10) {
  htmlFormatted = `
    <div style="padding: 15px; background: #e9ecef; border-radius: 5px;">
      <h4>📄 Risultati della ricerca:</h4>
      <div style="white-space: pre-line; line-height: 1.6;">${aiOutput}</div>
    </div>
  `;
  segnalazioniCount = 1;
}

// Header principale
let headerHtml = `<h3 style="color: #007bff;">📊 Analisi: ${segnalazioniCount} elemento/i trovato/i</h3>`;

// Box problema già noto/gestito
if (problemaGiaSegnalato) {
  headerHtml += `
  <div style="background: linear-gradient(135deg, #28a745, #20c997); color: white; padding: 20px; border-radius: 8px; margin-bottom: 20px; box-shadow: 0 4px 8px rgba(0,0,0,0.1);">
    <h4 style="margin-top: 0; display: flex; align-items: center; color: white;">
      <span style="font-size: 24px; margin-right: 10px;">✅</span>
      PROBLEMA GIÀ NOTO E IN GESTIONE
    </h4>
    <div style="background: rgba(255,255,255,0.2); padding: 15px; border-radius: 5px; margin-top: 15px;">
      ${problemaNottoInfo.numeroSegnalazioni ? `<p><strong>📈 Segnalazioni precedenti:</strong> ${problemaNottoInfo.numeroSegnalazioni}</p>` : ''}
      ${problemaNottoInfo.primoRilevamento ? `<p><strong>📅 Primo rilevamento:</strong> ${problemaNottoInfo.primoRilevamento}</p>` : ''}
      <p><strong>🏢 Ufficio responsabile:</strong> ${problemaNottoInfo.ufficioCompetente}</p>
      ${problemaNottoInfo.statoIntervento ? `<p><strong>⚙️ Stato intervento:</strong> ${problemaNottoInfo.statoIntervento}</p>` : ''}
      ${problemaNottoInfo.soluzioniAdottate ? `<p><strong>🔧 Soluzioni adottate:</strong> ${problemaNottoInfo.soluzioniAdottate}</p>` : ''}
      ${problemaNottoInfo.prossimiPassi ? `<p><strong>➡️ Prossimi passi:</strong> ${problemaNottoInfo.prossimiPassi}</p>` : ''}
    </div>
    ${problemaNottoInfo.comunicazione ? `
    <div style="background: rgba(255,255,255,0.3); padding: 12px; border-radius: 5px; margin-top: 10px; border-left: 4px solid white;">
      <strong>💬 Comunicazione:</strong> ${problemaNottoInfo.comunicazione}
    </div>` : ''}
  </div>`;
}

headerHtml += '<hr>';

const finalHtml = headerHtml + htmlFormatted;

// Footer con rassicurazioni per problemi noti
if (problemaGiaSegnalato) {
  const footerHtml = `
  <hr>
  <div style="background: #d4edda; padding: 15px; border-radius: 5px; margin-top: 20px; border-left: 4px solid #28a745;">
    <h5 style="color: #155724; margin-top: 0;">🏛️ MESSAGGIO DELL'AMMINISTRAZIONE:</h5>
    <p style="color: #155724; margin-bottom: 10px; font-size: 16px; line-height: 1.5;">
      <strong>L'amministrazione comunale conferma di essere a conoscenza del problema segnalato e di averlo già preso in carico.</strong>
    </p>
    <div style="background: rgba(21, 87, 36, 0.1); padding: 10px; border-radius: 3px;">
      <p style="color: #155724; margin: 0; font-style: italic;">
        "Ringraziamo i cittadini per le segnalazioni ricevute. Il nostro impegno è quello di garantire trasparenza e tempestività negli interventi per il benessere della comunità."
      </p>
    </div>
    <p style="color: #155724; margin-top: 10px; margin-bottom: 0; font-size: 14px;">
      <strong>Per aggiornamenti:</strong> È possibile contattare il ${problemaNottoInfo.ufficioCompetente} per informazioni sullo stato di avanzamento.
    </p>
  </div>`;

  const finalHtmlWithFooter = finalHtml + footerHtml;

  console.log('HTML con problema già noto generato:', finalHtmlWithFooter);

  return [{
    json: {
      resoconto_mail: finalHtmlWithFooter,
      count: segnalazioniCount,
      hasResults: segnalazioniCount > 0,
      problemaGiaNoto: true,
      ufficioCompetente: problemaNottoInfo.ufficioCompetente,
      statoIntervento: problemaNottoInfo.statoIntervento
    }
  }];
}

console.log('HTML generato:', finalHtml);

return [{
  json: {
    resoconto_mail: finalHtml,
    count: segnalazioniCount,
    hasResults: segnalazioniCount > 0,
    problemaGiaNoto: false
  }
}];

