        const vincoliPaesArray = vincoliPaesPart.split(',').map(v => v.trim().toLowerCase());
        const mappedVincoli = vincoliPaesArray.map(v => `<li>${vincoliMapping[v] || v}</li>`).join('');
        vincoliPaesList = `
