    const firstItem = inputData[0]?.json;

    if (!firstItem || !firstItem.id) {
        // Record vuoto o senza ID - foglio/mappale non esistente
        messaggioVincoli = `non risultano dati, il foglio catastale ${foglio} e il mappale ${mappale} non producono risultati (dati inesatti o particelle che nel frattempo sono state frazionate, eliminate, sostituite con nuove, ecc.)`;
